import React from 'react';
import './App.scss';
import Nav from "./components/Nav/Nav";
import About from "./components/About/About";
import { Login, SignUp } from "../src/components/index";
//import SearchBox from "./components/SearchBox/SearchBox";
// import axios from "axios";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";


class App extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            isLogginActive :true,
        }
    }

    changeState(){
        const {isLogginActive} =this.state;
         if(isLogginActive){
             this.Right.classList.remove("right");
             this.Right.classList.add("left");
         }else{
             this.Right.classList.remove("left");
             this.Right.classList.add("right");
         }

         this.setState(prevState => ({ isLogginActive: !prevState.isLogginActive}));
    }


    render() {
        const { isLogginActive } = this.state;
        const current = isLogginActive ? "SignUp" : "Login";
        const currentActive = isLogginActive ? "Login" : "SignUp";
        return(
            <div className="App">
                <div className="login">
                    <div className="container">
                        {isLogginActive && <Login containerRef={(ref) => this.current = ref }/> }
                        {!isLogginActive && <SignUp containerRef={(ref) => this.current = ref }/>}
                    </div>
                    <Right current={current} containerRef={(ref) => this.Right = ref } onClick={this.changeState.bind(this)}/>
                </div>
            </div>
        )
    }
}

const Right = (props) =>{
    return(
        <div className="right-side" ref={props.containerReference} onClick={props.onClick}>
            <div className="inner-container">
                <div className="text">{props.current}</div>
            </div>
        </div>
    )
};

